import classes from '../MealItem/MealItemForm.module.css'
import { Input } from '../../UI/Input'
export const MealItemForm = props =>{
    return(
        <form className={classes.form}>
            <Input input={props.meal} label={props.meal.label}></Input>
            <button>Add</button>

        </form>
    )
}
